import{_ as t}from"./chunks/getstarted.DMSoJ9Tg.js";import{_ as a,c as i,o as l,ag as o}from"./chunks/framework.3H72gWKR.js";const m=JSON.parse('{"title":"Quick Start","description":"","frontmatter":{},"headers":[],"relativePath":"guide/getting-started.md","filePath":"guide/getting-started.md"}'),s={name:"guide/getting-started.md"};function n(r,e,d,p,h,u){return l(),i("div",null,e[0]||(e[0]=[o(`<h1 id="quick-start" tabindex="-1">Quick Start <a class="header-anchor" href="#quick-start" aria-label="Permalink to &quot;Quick Start&quot;">​</a></h1><p>Welcome to the Logseq Slide Reveal. This is a simple guide to help you quickly understand and start using it.</p><h2 id="what-it-is" tabindex="-1">What It Is <a class="header-anchor" href="#what-it-is" aria-label="Permalink to &quot;What It Is&quot;">​</a></h2><p>Logseq Slide Reveal is a Logseq plugin that allows you to create Reveal.js-based slides within Logseq. Unlike Logseq&#39;s built-in slide solution, not every Block is converted into a slide page, but rather each top-level Block and its first-level Blocks together form a single slide.</p><p>Additionally, the principle of making slides with this plugin is that you design the slides you need where you need them, rather than converting every page you&#39;ve previously written into slides.</p><h2 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features&quot;">​</a></h2><ul><li>Each slide page is formed by each top-level Block and its first-level Blocks.</li><li>The plugin provides some Block properties to control the style of slides.</li><li>Supports setting slide themes.</li><li>Supports setting slide transition effects.</li><li>Supports drawing with a pen.</li><li>Supports shortcut keys.</li><li>Supports zooming.</li><li>Supports vertical navigation of slides.</li><li>Supports converting only the child elements of a certain Block into slides.</li><li>Supports previewing individual slide nodes.</li><li>And more.</li></ul><h2 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h2><h3 id="plugin-marketplace" tabindex="-1">Plugin Marketplace <a class="header-anchor" href="#plugin-marketplace" aria-label="Permalink to &quot;Plugin Marketplace&quot;">​</a></h3><p>Search for it in the plugin marketplace and install it from there.</p><h3 id="manual-installation" tabindex="-1">Manual Installation <a class="header-anchor" href="#manual-installation" aria-label="Permalink to &quot;Manual Installation&quot;">​</a></h3><ol><li>Download the latest zip file from the releases page.</li><li>Unzip the file into the folder where you want to store the plugin.</li><li>Turn on developer mode in Logseq.</li><li>Load the unzipped folder by clicking on the &quot;Load unpacked plugin&quot; button in the plugin modal.</li><li>You should now see the plugin has been installed.</li></ol><h2 id="getting-started" tabindex="-1">Getting Started <a class="header-anchor" href="#getting-started" aria-label="Permalink to &quot;Getting Started&quot;">​</a></h2><ol><li>Create a new page and, as usual, add your first Block:</li></ol><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>- # My Keynote</span></span>
<span class="line"><span>	- name@company</span></span></code></pre></div><ol start="2"><li>There is a slide icon in the top right corner of the page; click it, and you can view your slides.</li></ol><p><img src="`+t+'" alt=""></p><ol start="3"><li><p>That&#39;s it, you&#39;ve made your first slide.</p></li><li><p>Next, you can add more Blocks to the page, and then click the slide icon in the top right corner to view your slides. You may need to read more documentation to learn how to make your slides even cooler.</p></li></ol>',18)]))}const k=a(s,[["render",n]]);export{m as __pageData,k as default};
