import{_ as e,c as a,o,a5 as i}from"./chunks/framework.DwbewbAn.js";const m=JSON.parse('{"title":"Shortcuts","description":"","frontmatter":{},"headers":[],"relativePath":"guide/usage/shortcut.md","filePath":"guide/usage/shortcut.md"}'),t={name:"guide/usage/shortcut.md"},l=i(`<h1 id="shortcuts" tabindex="-1">Shortcuts <a class="header-anchor" href="#shortcuts" aria-label="Permalink to &quot;Shortcuts&quot;">​</a></h1><p>The plugin supports some shortcuts to enhance the slideshow experience.</p><h2 id="navigation" tabindex="-1">Navigation <a class="header-anchor" href="#navigation" aria-label="Permalink to &quot;Navigation&quot;">​</a></h2><ul><li><code>j</code>, <code>k</code>, <code>h</code>, <code>l</code>: Navigate up, down, left, right, similar to <code>Vim</code>.</li><li>Arrow keys: Same as above.</li><li>Spacebar: Next slide.</li><li><code>n</code>, <code>p</code>: Next slide, previous slide.</li></ul><h2 id="zoom" tabindex="-1">Zoom <a class="header-anchor" href="#zoom" aria-label="Permalink to &quot;Zoom&quot;">​</a></h2><p>Hold <code>Alt</code> key and click on the area to zoom in on a specific part of the slide; click again to restore original size.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Press <code>ESC</code> to enter overview mode, press <code>ESC</code> again to exit overview.</p><h2 id="pause" tabindex="-1">Pause <a class="header-anchor" href="#pause" aria-label="Permalink to &quot;Pause&quot;">​</a></h2><ul><li><code>b</code>: Blackout pause, press again to resume.</li></ul><h2 id="drawing-mode" tabindex="-1">Drawing Mode <a class="header-anchor" href="#drawing-mode" aria-label="Permalink to &quot;Drawing Mode&quot;">​</a></h2><ul><li><code>t</code>: Enter drawing mode, press again to exit drawing mode.</li><li><code>d</code>: Toggle select mode, drawing mode is not exited but drawing is halted.</li><li><code>1</code>~<code>9</code>: Choose color in drawing mode.</li><li><code>ctrl+z</code>: Undo.</li></ul><h2 id="exit" tabindex="-1">Exit <a class="header-anchor" href="#exit" aria-label="Permalink to &quot;Exit&quot;">​</a></h2><p><code>q</code>: Exit the slideshow.</p><h2 id="launching-slideshow" tabindex="-1">Launching Slideshow <a class="header-anchor" href="#launching-slideshow" aria-label="Permalink to &quot;Launching Slideshow&quot;">​</a></h2><p>To avoid conflicts with shortcuts from other plugins, no shortcut is bound by default, but you can edit the configuration file to bind a shortcut.</p><p><code>logseq-slide-reveal.json</code></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>{</span></span>
<span class="line"><span>  &quot;keyBindings&quot;: {</span></span>
<span class="line"><span>    &quot;openSlideReveal&quot;: &quot;mod+shift+l&quot;</span></span>
<span class="line"><span>  }</span></span>
<span class="line"><span>}</span></span></code></pre></div><h3 id="other-launch-methods" tabindex="-1">Other Launch Methods <a class="header-anchor" href="#other-launch-methods" aria-label="Permalink to &quot;Other Launch Methods&quot;">​</a></h3><ul><li>Click on the icon in the top right corner of the page to launch.</li><li>Type <code>Slide Reveal</code> in the command palette to launch.</li></ul><h3 id="launching-local-slides" tabindex="-1">Launching Local Slides <a class="header-anchor" href="#launching-local-slides" aria-label="Permalink to &quot;Launching Local Slides&quot;">​</a></h3><ul><li>Type <code>/Slide Reveal</code> in a block to launch local slides.</li><li>Choose <code>Slide Reveal</code> from the context menu of a block to launch local slides.</li></ul><h3 id="previewing-slides" tabindex="-1">Previewing Slides <a class="header-anchor" href="#previewing-slides" aria-label="Permalink to &quot;Previewing Slides&quot;">​</a></h3><ul><li>Type <code>/Slide Preview</code> in a block to preview slides.</li><li>Choose <code>Slide Preview</code> from the context menu of a block to preview slides.</li></ul>`,24),s=[l];function n(d,c,r,h,u,p){return o(),a("div",null,s)}const v=e(t,[["render",n]]);export{m as __pageData,v as default};
